import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({ name: "", phone: "", email: "", password: "", username: "" });
  const [message, setMessage] = useState("");

  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const url = isLogin
      ? "http://127.0.0.1:8000/api/login/"                  // ‚úÖ Login using Phone/Email
      : "http://127.0.0.1:8000/api/register/"       // ‚úÖ Signup Save

    const payload = isLogin
      ? { username: form.username, password: form.password } // username = phone or email
      : { name: form.name, phone: form.phone, email: form.email, password: form.password };

    const response = await fetch(url, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    const data = await response.json();

    if (response.ok) {
      if (isLogin) {
        // ‚úÖ Store login tokens + name
        localStorage.setItem("access_token", data.access);
        localStorage.setItem("refresh_token", data.refresh);
        localStorage.setItem("username", data.username);   // first_name from backend

        setMessage("‚úÖ Login successful!");

        // ‚úÖ Update Navbar instantly
        window.dispatchEvent(new Event("storage"));

        // ‚úÖ Redirect
        setTimeout(() => navigate("/"), 500);

      } else {
        setMessage("üéâ Account created successfully! Please login now.");
        setIsLogin(true);
      }
    } else {
      setMessage(data.error || "‚ùå Something went wrong.");
    }
  };

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h2 style={styles.header}>{isLogin ? "Login" : "Sign Up"}</h2>

        {message && <p style={styles.message}>{message}</p>}

        <form onSubmit={handleSubmit} style={styles.form}>

          {/* ‚úÖ SIGNUP FIELDS */}
          {!isLogin && (
            <>
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={form.name}
                onChange={handleChange}
                required
                style={styles.input}
              />

              <input
                type="text"
                name="phone"
                placeholder="Phone Number"
                value={form.phone}
                onChange={handleChange}
                required
                style={styles.input}
              />

              <input
                type="email"
                name="email"
                placeholder="Email Address"
                value={form.email}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </>
          )}

          {/* ‚úÖ LOGIN FIELD (Email or Phone) */}
          {isLogin && (
            <input
              type="text"
              name="username"
              placeholder="Email or Phone"
              value={form.username}
              onChange={handleChange}
              required
              style={styles.input}
            />
          )}

          {/* ‚úÖ PASSWORD FIELD (shared) */}
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={form.password}
            onChange={handleChange}
            required
            style={styles.input}
          />

          <button type="submit" style={styles.button}>
            {isLogin ? "Login" : "Sign Up"}
          </button>
        </form>

        <p style={styles.switchText}>
          {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
          <span
            style={styles.link}
            onClick={() => {
              setIsLogin(!isLogin);
              setMessage("");
            }}
          >
            {isLogin ? "Sign up here" : "Login here"}
          </span>
        </p>
      </div>
    </div>
  );
}

const styles = {
  page: {
    fontFamily: "'Poppins', sans-serif",
    backgroundColor: "#fffaf6",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  card: {
    backgroundColor: "#fff",
    padding: "2rem",
    borderRadius: "16px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
    width: "350px",
    textAlign: "center",
  },
  header: {
    color: "#d35400",
    fontWeight: "600",
    marginBottom: "1rem",
  },
  input: {
    width: "100%",
    padding: "0.8rem",
    marginBottom: "1rem",
    border: "1px solid #f3c9a9",
    borderRadius: "8px",
    backgroundColor: "#fffaf6",
  },
  button: {
    backgroundColor: "#d35400",
    color: "white",
    border: "none",
    borderRadius: "8px",
    padding: "0.9rem",
    width: "100%",
    fontWeight: "600",
    cursor: "pointer",
  },
  switchText: { fontSize: "0.9rem", marginTop: "1rem", color: "#555" },
  link: { color: "#d35400", fontWeight: "600", cursor: "pointer" },
  message: { color: "#d35400", marginBottom: "1rem", fontSize: "0.9rem" },
};

export default AuthPage;
