import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function SareeList() {
  const [sarees, setSarees] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/sarees/")
      .then((res) => res.json())
      .then((data) => setSarees(data))
      .catch((err) => console.error("Error fetching sarees:", err));
  }, []);

  return (
    <div style={{ fontFamily: "'Poppins', sans-serif" }}>
      {/* ðŸª” Hero Banner */}
      <section
        style={{
          backgroundImage: "url('/banner1.png')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          color: "#fff",
          textAlign: "center",
          padding: "5rem 1rem",
          borderRadius: "10px",
          marginBottom: "3rem",
          position: "relative",
        }}
      >
        <div
          style={{
            backgroundColor: "rgba(0, 0, 0, 0.45)",
            position: "absolute",
            inset: 0,
            borderRadius: "10px",
          }}
        ></div>
        <div style={{ position: "relative", zIndex: 1 }}>
          <h1 style={{ fontSize: "2.7rem", fontWeight: "700", color: "#fffaf5" }}>
            Subhadra Handlooms
          </h1>
          <p style={{ fontSize: "1.15rem", color: "#ffe9d6" }}>
            Authentic Odisha Handlooms âœ¨
          </p>
        </div>
      </section>

      {/* ðŸ’Ž Saree Grid */}
      <div id="collection" className="product-list">
        {sarees.map((saree) => (
          <div className="product-card" key={saree.id}>
            <Link to={`/saree/${saree.id}`}>
              <img
                src={saree.image}
                alt={saree.name}
                className="product-image"
                onError={(e) =>
                  (e.target.src = "https://via.placeholder.com/300")
                }
              />
            </Link>

            <h3>{saree.name}</h3>
            <p className="price">â‚¹{saree.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SareeList;
