import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

function Navbar() {
  const [username, setUsername] = useState(null);
  const navigate = useNavigate();

  // Check login status on load
  useEffect(() => {
  const updateUser = () => {
    const token = localStorage.getItem("access_token");
    const storedUser = localStorage.getItem("username");
    if (token && storedUser) {
      setUsername(storedUser);
    } else {
      setUsername(null);
    }
  };

  updateUser(); // Run immediately on load

  window.addEventListener("storage", updateUser); // ‚úÖ Listen for login/logout changes
  return () => window.removeEventListener("storage", updateUser);
}, []);

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem("access_token");
    localStorage.removeItem("refresh_token");
    localStorage.removeItem("username");
    setUsername(null);
    navigate("/auth");
  };

  return (
    <header style={styles.header}>
      <div style={styles.logoContainer}>
        <Link to="/" style={styles.logoText}>
          Subhadra Handlooms
        </Link>
      </div>

      <nav style={styles.navLinks}>
        <Link to="/" style={styles.loginBtn}>
          Home
        </Link>
        <Link to="/cart" style={styles.loginBtn}>
          üõçÔ∏è Cart
        </Link>
        <Link to="/profile" style={styles.loginBtn}>My Profile</Link>

        {/* üë§ Auth Section */}
        {username ? (
          <div style={styles.userSection}>
            <span style={styles.username}>üë§ Hello, {username}</span>
            <button onClick={handleLogout} style={styles.logoutBtn}>
              Logout
            </button>
          </div>
        ) : (
          <Link to="/auth" style={styles.loginBtn}>
            üë§ Login
          </Link>
        )}
      </nav>
    </header>
  );
}

/* üé® Inline Styles */
const styles = {
  header: {
    backgroundColor: "#fff",
    boxShadow: "0 2px 6px rgba(0, 0, 0, 0.1)",
    padding: "1rem 2rem",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    fontFamily: "'Poppins', sans-serif",
    position: "sticky",
    top: 0,
    zIndex: 100,
  },
  logoContainer: { display: "flex", alignItems: "center" },
  logoText: {
    color: "#d35400",
    fontWeight: "700",
    fontSize: "1.5rem",
    textDecoration: "none",
  },
  navLinks: {
    display: "flex",
    alignItems: "center",
    gap: "1.5rem",
  },
  link: {
    color: "#333",
    fontWeight: "500",
    textDecoration: "none",
    transition: "color 0.3s",
  },
  linkHover: {
    color: "#d35400",
  },
  userSection: {
    display: "flex",
    alignItems: "center",
    gap: "0.8rem",
  },
  username: {
    color: "#333",
    fontWeight: "500",
  },
  logoutBtn: {
    backgroundColor: "#d35400",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    padding: "0.4rem 0.8rem",
    cursor: "pointer",
    fontSize: "0.85rem",
    fontWeight: "500",
  },
  loginBtn: {
    color: "#d35400",
    fontWeight: "600",
    textDecoration: "none",
    fontSize: "0.95rem",
  },
};

export default Navbar;
